import React,{ useState }  from 'react'
import './SideBar.css'
import SideBarData from './SideBarData'
import { Link } from 'react-router-dom'

function SideBar() {
  const [activeIndex, setActiveIndex] = useState(0);

  const handleLinkClick = (index) => {
    setActiveIndex(index);
  };

  return (
    <div className="w-1/5 h-screen bg-transparent fixed">
      <nav className="bg-gray-800 h-full">
      <img src="assets/logo.png" alt="" className="m-img" />
        <div className="flex items-center justify-center mb-4">  
        </div>
        <ul className="flex flex-col">
          {SideBarData.map((item, index) => (
            <li
            key={index}
            className={`h-16 flex items-center ${
              index === activeIndex ? "bg-blue-500" : ""
            }`}

            >
              <Link
                to={item.path}
                className="w-full h-full flex items-center px-4 text-white transition-colors duration-300 ease-in-out hover:bg-slate-300"
                onClick={() => handleLinkClick(index)}
              >
                <span>{item.title}</span>
              </Link>
            </li>
          ))}
        </ul>
      </nav>
    </div>
  );
}

export default SideBar;
